import React, { useEffect, useState } from "react";
import axios from "axios";

function Showtimes({ movieId, onBack }) {
  const [showtimes, setShowtimes] = useState([]);
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    if (!movieId) return;
    axios.get(`http://localhost:8080/movies/${movieId}/showtimes`)
      .then(r => setShowtimes(r.data));
    axios.get(`http://localhost:8080/movies/${movieId}`)
      .then(r => setMovie(r.data));
  }, [movieId]);

  if (!movieId) return null;

  return (
    <div className="showtimes-container">
      <button onClick={onBack}>‚Üê Back</button>
      <h2>üéüÔ∏è Showtimes for: {movie ? movie.title : "..."}</h2>

      <div className="showtime-list">
        {showtimes.length === 0 ? (
          <p>No showtimes available.</p>
        ) : (
          showtimes.map((s) => (
            <div key={s.id} className="showtime-card">
              <p><b>Date:</b> {s.showDate}</p>
              <p><b>Time:</b> {s.showTime}</p>
              <p><b>Seats:</b> {s.totalSeats}</p>
              <button onClick={() => alert("Seat selection coming soon!")}>Select Seats</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Showtimes;
