import React, { useState } from "react";
import Login from "./Login";
import Register from "./Register";
import MoviesList from "./MoviesList";
import Showtimes from "./Showtimes";
import "./App.css";

function App() {
  const [userLoggedIn, setUserLoggedIn] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [selectedMovieId, setSelectedMovieId] = useState(null);

  // when user successfully logs in
  const handleLoginSuccess = () => setUserLoggedIn(true);

  if (!userLoggedIn) {
    return (
      <div className="auth-container">
        {!showRegister ? (
          <>
            <Login />
            <p>
              Don't have an account?{" "}
              <button onClick={() => setShowRegister(true)}>Register</button>
            </p>
          </>
        ) : (
          <>
            <Register />
            <p>
              Already have an account?{" "}
              <button onClick={() => setShowRegister(false)}>Login</button>
            </p>
          </>
        )}
        {/* Dummy login success button for now */}
        <button
          onClick={handleLoginSuccess}
          style={{ marginTop: "15px", background: "green" }}
        >
          Continue to Movies (Skip Login)
        </button>
      </div>
    );
  }

  // once logged in â†’ show movies
  return (
    <div className="app">
      <h1 className="title">ðŸŽ¥ Online Ticket Booking</h1>

      {!selectedMovieId ? (
        <MoviesList onSelect={(id) => setSelectedMovieId(id)} />
      ) : (
        <Showtimes
          movieId={selectedMovieId}
          onBack={() => setSelectedMovieId(null)}
        />
      )}
    </div>
  );
}

export default App;
