import React, { useEffect, useState } from "react";
import axios from "axios";

function MoviesList({ onSelect }) {
  const [movies, setMovies] = useState([]);
  const [q, setQ] = useState("");

  useEffect(() => {
    fetchAll();
  }, []);

  const fetchAll = async () => {
    const res = await axios.get("http://localhost:8080/movies/all");
    setMovies(res.data);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!q) return fetchAll();
    const res = await axios.get(`http://localhost:8080/movies/search?q=${encodeURIComponent(q)}`);
    setMovies(res.data);
  };

  return (
    <div className="movies-container">
      <h2>ðŸŽ¬ Movies & Events</h2>

      <form onSubmit={handleSearch} className="search-bar">
        <input
          placeholder="Search by title / genre / location"
          value={q}
          onChange={(e) => setQ(e.target.value)}
        />
        <button type="submit">Search</button>
        <button type="button" onClick={() => { setQ(""); fetchAll(); }}>Reset</button>
      </form>

      <div className="movie-list">
        {movies.map((m) => (
          <div key={m.id} className="movie-card">
            <h3>{m.title}</h3>
            <p><b>Genre:</b> {m.genre}</p>
            <p><b>Location:</b> {m.location}</p>
            <p>{m.description}</p>
            <button onClick={() => onSelect(m.id)}>View Showtimes</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MoviesList;
